     setCount(prev => prev + 1);
     sethidden(prev => !prev);
    }
    return (       
    <div>
        <input type="text" name="" className={hide ? "" : "d-none"} id="" placeholder={count} />
        <button className="btn btn-primary" onClick={handleClick}>add value</button>
    </div>
    )
}

export default Counter  ;
